(1, 'VISI, MISI, DAN MOTTO', 'visi-dan-misi', '', '<p>Berikut Visi, Misi, dan Motto UPTD Laboratorium Lingkungan.</p>\r\n', '', 1, 0, 0, 4, '2018-07-13 02:49:19', 1, '', '2021-03-07 16:19:46', 6, 0, '0000-00-00 00:00:00', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'd4c64a4f0f9a404a4cc243e6f5590d55.jpg', '', '', '', 1, 0, 0, '', '', 0, 0, '', NULL),
(6, 'Kebijakan Mutu', 'kebijakan', '', '<p>Berikut adalah kebijakan mutu dari UPT Laboratorium Lingkungan DLH Kabupaten Banyuwangi</p>\r\n', '', 1, 0, 0, 4, '2018-07-15 09:46:10', 1, '', '2021-03-15 08:10:29', 6, 0, '0000-00-00 00:00:00', '0000-00-00 00:00:00', '0000-00-00 00:00:00', '3f29c868eb4a5e9bf335e16eafe54b0e.jpg', '', '', '', 1, 0, 0, 'kebijakan mutu', '', 0, 0, '', NULL),
(7, 'Struktur Organisasi Dan Personil ', 'struktur-organisasi-dan-personil', '', '<p>Struktur Organisasi Dan Personil dari UPT Laboratorium Lingkungan DLH Kabupaten Banyuwangi tertera sebagaimana gambar di atas.</p>\r\n', '', 1, 0, 0, 4, '2018-07-15 09:46:59', 1, '', '2021-03-08 12:21:13', 6, 0, '0000-00-00 00:00:00', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'f9c0758dd9e6042a5f3e55b30eec087e.jpg', '', '', '', 1, 0, 0, 'Struktur organisasi', '', 0, 0, '', NULL),
(8, 'Tugas Pokok Dan Fungsi', 'tugas-poko-dan-fungsi', '', '<h2>Tugas Pokok Dan Fungsi UPT Laboratorium Lingkungan DLH Banyuwangi meliputi :</h2>\r\n\r\n<ol>\r\n	<li>Pelayanan pemeriksaan contoh uji (sampel) dan pengujian parameter kualitas lingkungan.</li>\r\n	<li>Pelayanan Pengambilan contoh uji (sampel)</li>\r\n	<li>Pelayanan jasa pengujian, penelitian, pendidikan dan pelatihan teknis di bidang laboratorium lingkungan</li>\r\n	<li>Pengembangan teknis dan metode analisa laboratorium lingkungan sesuai ISO/IEC 17025 : 2017</li>\r\n	<li>Pemungutan atas retribusi jasa pengujian</li>\r\n</ol>\r\n', '', 1, 0, 0, 4, '2018-07-15 09:47:29', 1, '', '2021-03-08 14:08:46', 6, 0, '0000-00-00 00:00:00', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'f7e1d65023b4ab3d2e0e19455cb1e1ea.jpg', '', '', '', 1, 0, 0, 'Tugas pokok dan fungsi', '', 0, 0, '', NULL),
(9, 'Landasan Hukum', 'landasan-hukum', '', '<p>Landasan Hukum berdirinya UPT Laboratorium Lingkungan DLH Kab. Banyuwangi didasarkan pada Peraturan Bupati No. 57 Tahun 2017 Tentang Rincian Tugas, Fungsi Dan Tata Kerja Unit Pelaksana Teknis Dinas Laboratorium Lingkungan Pada Dinas Lingkungan Hidup Kabupaten Banyuwangi. Adapun isi Perbup No. 57 Tahun 2017 telah dicantumkan pada dokumen di bawah.</p>\r\n', '', 1, 0, 0, 4, '2018-07-15 09:48:01', 1, '', '2021-03-18 15:17:07', 6, 0, '0000-00-00 00:00:00', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'ae02b2334272408b92b472a2599632af.jpg', '1f92c7ba47b6a30ebaa556c02fb090d4.pdf', '', '', 1, 0, 0, '', '', 0, 0, '', NULL),
(10, 'Pengujian ', 'pengujian', '', '<div style=\"background:#eee; border:1px solid #ccc; padding:5px 10px\">UPTD Laboratorium Lingkungan DLH Kab. Banyuwangi melayani pengujian kualitas air dan udara. Laboratorium kami telah teregistrasi sebagai laboratorium penguji terakreditasi KAN LP-976 IDN.</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div style=\"background:#eee; border:1px solid #ccc; padding:5px 10px\">Untuk kualitas air meliputi matriks air limbah, air permukaan, air minum, air bersih, dan air laut. Dan matriks yang sudah masuk dalam lingkup akreditasi KAN adalah Air limbah, air permukaan, air minum dan air bersih.</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div style=\"background:#eee; border:1px solid #ccc; padding:5px 10px\">Untuk kualitas udara meliputi matriks udara ambien dan udara emisi sumber tidak bergerak. Dalam pengujian kualitas udara ambien, biasanya juga disertai pengukuran lapangan yaitu TSP, kebisingan, kelembapan, dan getaran.</div>\r\n', '', 1, 0, 0, 4, '2018-07-15 09:50:43', 1, '', '2021-03-15 15:43:58', 6, 0, '0000-00-00 00:00:00', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'b2e5146e1d451018395b3fd814baf994.jpg', '', '', '', 1, 0, 0, 'Pengujian', '', 0, 0, '', NULL),
(11, 'Alur Pengujian', 'alur-pengujian', '', '<p>Alur pengujian</p>\r\n', '', 1, 0, 0, 4, '2018-07-15 09:50:56', 1, '', '2021-03-07 17:40:09', 6, 0, '0000-00-00 00:00:00', '0000-00-00 00:00:00', '0000-00-00 00:00:00', '', '', '', '', 1, 0, 0, '', '', 0, 0, '', NULL),
(12, 'Parameter Pengujian', 'parameter-pengujian', '', '<ul>\r\n	<li>Kemampuan Parameter Pengujian UPT Laboratorium Lingkungan DLH Kab. Banyuwangi terdiri dari parameter pengujian matriks air, udara dan B3. Adapun parameter yang mampu kami layani telah tertera di gambar di atas.&nbsp;</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Dari keseluruhan parameter yang mampu diuji, 30 parameter diantaranya sudah terakreditasi oleh KAN. Sehingga sebagian besar laporan hasil uji yang diberikan ke pelanggan sudah mencantumkan logo KAN di dalamnya.&nbsp;</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Kami senantiasa terus berbenah demi meningkatkan kemampuan dan kualitas pengujian kami dengan menerapkan jaminan mutu laboratorium sebagaimana yang telah dipersyaratkan oleh KAN.</li>\r\n</ul>\r\n', '', 1, 0, 0, 4, '2018-07-15 09:51:11', 1, '', '2021-03-15 15:50:15', 6, 0, '0000-00-00 00:00:00', '0000-00-00 00:00:00', '0000-00-00 00:00:00', '606e8fc2cc84b5c9849edf794a7fd207.jpg', '', '', '', 1, 0, 0, '', '', 0, 0, '', NULL),
(13, 'PP / Perpres', 'perpres', '', '<p>Berikut Ini Regulasi atau Produk Hukum dari Peraturan Pemerintah / Perpres / UU :</p>\r\n\r\n<ol>\r\n  <li><a href=\"https://drive.google.com/file/d/1wg1bhHIN7HnjRNKe4rZAGVEVHddbHf1_/view?usp=sharing\" target=\"_blank\">PP No. 22 Tahun 2021 Tentang Penyelenggaraan Perlindungan Dan Pengelolaan Lingkungan Hidup</a></li>\r\n  <li><a href=\"https://drive.google.com/file/d/18RAKQAwz5NSbGARQwoTaVZSkQGOG0Oze/view?usp=sharing\" target=\"_blank\">Lampiran VI PP Nomor 22 Tahun 2021</a></li>\r\n  <li><a href=\"https://drive.google.com/file/d/10tJH_U9US46pNFlrv3qr80zyHY2JCwia/view?usp=sharing\" target=\"_blank\">Lampiran VII PP Nomor 22 Tahun 2021</a></li>\r\n  <li><a href=\"https://drive.google.com/file/d/1FDcNy65QVsj0HWV46PPUI6QQA-AtlCIB/view?usp=sharing\" target=\"_blank\">Lampiran VIII PP Nomor 22 Tahun 2021</a></li>\r\n  <li><a href=\"https://drive.google.com/file/d/1d-JU3p4ydmwQvzUydyf1xvdEQ_owswHY/view?usp=sharing\" target=\"_blank\">PP No. 101 Tahun 2014 Tentang Pengelolaan Limbah B3</a></li>\r\n  <li><a href=\"https://drive.google.com/file/d/18utNQMp9-oSDjCmcLG2e2KknlK3EwQ4T/view?usp=sharing\" target=\"_blank\">Lampiran PP No. 101 Tahun 2014</a></li>\r\n  <li><a href=\"https://drive.google.com/file/d/1gXSoknkFct80_7NG2JWjgk-eXvgp9qYM/view?usp=sharing\" target=\"_blank\"> UU No. 32 Tahun 2009 Tentang Perlindungan Dan Pengelolaan Lingkungan Hidup</a></li>\r\n</ol>\r\n\r\n<p>&nbsp;</p>', '', 1, 0, 0, 4, '2018-07-15 09:51:23', 1, '', '2021-03-18 15:48:35', 6, 0, '0000-00-00 00:00:00', '0000-00-00 00:00:00', '0000-00-00 00:00:00', '', '', '', '', 1, 0, 0, '', '', 0, 0, '', NULL),
